
public class DoWhileLoopPractice1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[]={25,21,78,90,56};
        //i starts with 0 as array index starts with 0
        int i=0;
        do{
             System.out.println(arr[i]);
             i++;
        }while(i<4);
        
	}

}
